<template>
  <div class="ez-avatar-wrapper" :style="wrapperStyle" @click="$emit('click')">
    <!-- #ifdef MP-WEIXIN -->
    <img :src="src" class="ez-avatar" alt="avatar" v-if="src" />
    <open-data class="ez-avatar" type="userAvatarUrl" v-else></open-data>
    <!-- #endif -->
    <!-- #ifdef H5 -->
    <img
      src="data:image/png;base64,"
      class="ez-avatar"
      alt="avatar"
    />
    <!-- #endif -->
  </div>
</template>

<script>
/**
 * 头像
 */

export default {
  name: 'ezAvatar',
  components: {},
  props: {
    // 头像url路径
    src: {
      type: [String],
      default: '',
    },
    size: {
      type: [String, Number],
      default: '64rpx',
    },
  },
  data() {
    return {}
  },

  created() {},
  methods: {},
  computed: {
    wrapperStyle() {
      let str = ''
      const size = this.size

      if (size) {
        if (typeof size === 'string') {
          str += `width: ${size}; `
          str += `height: ${size}; `
        }
        if (typeof size === 'number') {
          str += `width: ${size}rpx; `
          str += `height: ${size}rpx; `
        }
      }
      return str
    },
  },
  watch: {},
}
</script>
<style lang="scss" scoped>
.ez-avatar-wrapper {
  border-radius: 50%;
  overflow: hidden;
  margin: auto;
}
.ez-avatar {
  display: block;
  width: 100%;
  height: 100%;
}
</style>
